#!/bin/bash

set -e
set -x

# rm -rf output

# Single test
RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm setup

RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm single-prove --public 133:i64 --private :bytes-packed
RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm single-verify --public 133:i64 --proof output/zkwasm.0.transcript.data

RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm aggregate-prove --public 133:i64
RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm aggregate-verify --proof output/aggregate-circuit.0.transcript.data  --instances output/aggregate-circuit.0.instance.data
RUST_LOG=info cargo run --release -- --function zkmain --output ./output --wasm wasm/rlp.wasm solidity-aggregate-verifier --proof output/aggregate-circuit.0.transcript.data  --instances output/aggregate-circuit.0.instance.data
